import styled from 'styled-components/native';

export const StyledSmallText = styled.Text`
    color: #00C6CF;
    font-size: 24px;
`;

export const StyledRegularText = styled.Text`
    color: #00C6CF;
    font-size: 36px;
`;

export const StyledButtonText = styled(StyledRegularText)`
    font-size: 28px;
`;

export const StyledRoundedButton = styled.TouchableOpacity`
    alignItems: center;
    backgroundColor: #151515;
    borderColor: #00C6CF;
    borderRadius: 28;
    borderWidth: 2;
    width: 175;
`;

export const StyledButtonGroup = styled.View`
    flexDirection: row;
    flexWrap: wrap;
    justifyContent: space-around;
    rowGap: 10;
`;
