import React, {useState} from 'react';
import { Text, TextInput, VirtualizedList } from 'react-native';
import { Picker } from '@react-native-picker/picker';
import seedMovies from '../movieData';

const DisplayAll = () => {
    const [sortOption, setSortOption] = useState("Title");
    //TODO when sort option is selected, send req to server for new sorted list
    const { movies } = seedMovies;
    const sortList = movies.reduce((obj, movie) => {
        const firstLetter = movie.title[0].toUpperCase();
        return {
            ...obj,
            [firstLetter]: [...(obj[firstLetter] || []), movie]
        }
    }, {});
    const sections = Object.keys(sortList).sort().map(letter => ({
        heading: letter,
        data: sortList[letter]
    }));
    console.log(sections);

    return (
        <>
            <Text>{sortOption}</Text>
            <Text>Heading</Text>
                <Picker
                    selectedValue={sortOption}
                    style={{ height: 50, width: 100 }}
                    onValueChange={(itemValue, itemIndex) =>
                        setOption({ sortOption: itemValue })
                    }>
                    <Picker.Item label="Java" value="java" />
                    <Picker.Item label="JavaScript" value="js" />
                </Picker>
                
                <TextInput
                    onChangeText={console.log('text => onChangeText(text)')}
                    value={console.log('value')}
                />
            {/* <VirtualizedList /> */}
        </>
    );
}

export default DisplayAll;
