import React, {useState} from 'react';
import { Text, TextInput, SectionList } from 'react-native';
import { Picker } from '@react-native-picker/picker';
import seedMovies from '../movieData';

const DisplayAll = () => {
    const [sortOption, setSortOption] = useState("Title");
    //TODO when sort option is selected, send req to server for new sorted list
    const { movies } = seedMovies;
    // take list of movies and modify into an object where the key is the first letter of the title and the value is an array of all movies starting with that letter
    const sortTitle = movies.reduce((obj, movie) => {
        let firstLetter = movie.title[0].toUpperCase();
        const ascii = firstLetter.charCodeAt();
        if (ascii >= 48 && ascii <= 57) {
            firstLetter = '#';
        }
        return {
            ...obj,
            [firstLetter]: [...(obj[firstLetter] || []), movie]
        }
    }, {});

    const sortGenre = movies.reduce((obj, movie) => {
        movie.genre.forEach(genre => {
            obj[genre] = [...(obj[genre] || []), movie.title]
        });
        return obj;
    }, {});

    const sectionedGenre = Object.keys(sortGenre).sort().map(genre => ({
        heading: genre,
        data: sortGenre[genre]
    }));
  
    // sort new object by keys and format to specifications of 'SectionList'
    const sectionedTitle = Object.keys(sortTitle).sort().map(letter => ({
        heading: letter,
        data: sortTitle[letter]
    }));

    const sectionedList = (sortOption) => {
        Object.keys(sortOption).sort().map(item => ({
            heading: item,
            data: sortOption[item]
        }));
    }

    console.log('title: ', sectionedTitle);
    console.log('title fn: ', sectionedList(sortTitle));
    console.log('genre: ', sectionedGenre);
    console.log('genre fn: ', sectionedList(sortGenre));

    return (
        <>
            <Text>{sortOption}</Text>
            <Text>Heading</Text>
                <Picker
                    selectedValue={sortOption}
                    style={{ height: 50, width: 100 }}
                    onValueChange={(itemValue, itemIndex) =>
                        setSortOption(itemValue)
                    }>
                    <Picker.Item label="Title" value="title" />
                    <Picker.Item label="Genre" value="genre" />
                    <Picker.Item label="Format" value="format" />
                    <Picker.Item label="Actor" value="actor" /> //TODO get list of actor last names and sorted
                    <Picker.Item label="Director" value="director" />
                </Picker>
                
                <TextInput
                    onChangeText={() => console.log('text => onChangeText(text)')}
                    value={console.log('value')}
                />
            <SectionList
                sections={sectionedTitle}
                renderItem={({ item }) => <Text>{item.title}</Text>}
                renderSectionHeader={({ section: { heading } }) => <Text>{heading}</Text>}
            />
        </>
    );
}

export default DisplayAll;
