import React, {useState} from 'react';
import { Button, StyleSheet, Text, TouchableOpacity } from 'react-native';

const Home = () => {
    const [movies, setMovies] = useState(true);

    return (
        <>
            <Text>Movie Library</Text>
            {movies &&
                <>
                    <Text>Recently Added</Text>
                    {/* <Movie /> */}
                </>
            }

            <Text>View By</Text>
            <TouchableOpacity style={styles.button}>
                <Text>Title</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Genre</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Actor</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Format</Text>
            </TouchableOpacity>

            <Button title="Pick random movie" />
        </>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        marginHorizontal: 16,
    },
    button: {
        color: 'red',
        textAlign: 'right',
        marginVertical: 8,
    }
});

export default Home;
