import React, {useState, useEffect} from 'react';
import { Image, Pressable, StyleSheet, Text, TouchableOpacity } from 'react-native';
import seedMovies from '../movieData';
import Movie from './movie';

const Home = ({navigation}) => {
    const [movies, setMovies] = useState(null);

    useEffect(() => { 
        setMovies(seedMovies);
    }, [movies]);

    return (
        <>
            <Text>Movie Library</Text>
            {movies &&
                <>
                    <Text>Recently Added</Text>
                    {movies.movies.map(movie =>
                        <Pressable onPress={() => navigation.navigate('Movie Details', {movie})}>
                            <Image 
                                key={movie.id}
                                style={{height: 50, width: 50}}
                                source={{ uri: movie.imageURL }}
                            />
                        </Pressable>
                    )}    
                </>
            }

            <Text>View By</Text>
            <TouchableOpacity>
                <Text>Title</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Genre</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Actor</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Format</Text>
            </TouchableOpacity>

            <TouchableOpacity>
                <Text>Pick random movie</Text>
            </TouchableOpacity>
        </>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        marginHorizontal: 16,
    }
});

export default Home;
