import React, {useState, useEffect} from 'react';
import { Image, Pressable, SafeAreaView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import seedMovies from '../../movieData';

const newMovies = seedMovies.movies;

const HomeScreen = ({navigation}) => {
    const [movies, setMovies] = useState(null);

    const handleRandomMoviePress = () => {
        //TODO make sure value doesn't duplicate
        const randomIndex = Math.floor(Math.random() * Math.floor(movies.length));
        navigation.navigate('Movie Details', { movieDetails: movies[randomIndex] });
    }

    useEffect(() => { 
        setMovies(newMovies);
    }, []);

    return (
        <SafeAreaView style={styles.container}>
            <ScrollView contentContainerStyle={{ alignItems: 'center'}}>
                <Text>Movie Library</Text>
                {movies &&
                    <>
                        <Text>Recently Added</Text>
                        <View style={styles.row}>
                            {movies.map(movie =>
                                <Pressable key={movie.id} onPress={() => navigation.navigate('Movie Details', {movieDetails: movie})}>
                                    <Image 
                                        style={{height: 50, width: 50}}
                                        source={{ uri: movie.imageURL }}
                                    />
                                </Pressable>
                            )}    
                        </View>
                    </>
                }

                <Text>View By</Text>
                <TouchableOpacity>
                    <Text>Title</Text>
                </TouchableOpacity>
                <TouchableOpacity>
                    <Text>Genre</Text>
                </TouchableOpacity>
                <TouchableOpacity>
                    <Text>Actor</Text>
                </TouchableOpacity>
                <TouchableOpacity>
                    <Text>Format</Text>
                </TouchableOpacity>

                <TouchableOpacity onPress={handleRandomMoviePress}>
                    <Text>Pick random movie</Text>
                </TouchableOpacity>
            </ScrollView>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        alignItems: 'center',
        flex: 1,
        marginHorizontal: 16,
    },
    row: {
        flexDirection: 'row',
        flexWrap: 'wrap'
    }
});

export default HomeScreen;
