import React, {useState, useEffect} from 'react';
import { Button, StyleSheet, Text, TouchableOpacity } from 'react-native';
import seedMovies from '../movieData';
import Movie from './movie';

const Home = () => {
    const [movies, setMovies] = useState(null);

    useEffect(() => { 
        setMovies(seedMovies);
        console.log(movies);
    }, [movies]);

    return (
        <>
            <Text>Movie Library</Text>
            {movies &&
                <>
                    <Text>Recently Added</Text>
                    {movies.movies.map(movie => 
                        <Movie />
                    )}    
                </>
            }

            <Text>View By</Text>
            <TouchableOpacity>
                <Text>Title</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Genre</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Actor</Text>
            </TouchableOpacity>
            <TouchableOpacity>
                <Text>Format</Text>
            </TouchableOpacity>

            <TouchableOpacity>
                <Text>Pick random movie</Text>
            </TouchableOpacity>
        </>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        marginHorizontal: 16,
    }
});

export default Home;
