import React, { useCallback, useEffect, useState } from 'react';
import { debounce } from 'lodash';
import { Picker } from '@react-native-picker/picker';
import { StyledButtonText, StyledCenteredSafeArea, StyledRoundedButton, StyledRowView, StyledSmallText, StyledTextInput } from './config/globalStyles';

const AddItem = () => {
    const [format, setFormat] = useState(null);
    const [pictureQuality, setPictureQuality] = useState(null);
    const [search, setSearch] = useState(null);
    const [searchResults, setSearchResults] = useState(null);

    useEffect(() => {
        search && debouncedGetDetails(search);
    }, [search]);

    // send the query parameters but do the api call from backend
    // MAKE SURE TO USE API KEY AS A HEADER AND NOT IN THE URL
    const getMoviesFromApiAsync = async (text) => {
        try {
            let response = await fetch(`https://api.themoviedb.org/3/search/multi?api_key=c3f3d49f497ac5857c73ee93695e9da2&language=en-US&query=${text}&page=1`);
            let json = await response.json();
            return json.results;
        } catch (error) {
            console.error(error);
        }
    };

    const getDetails = (text) => {
        getMoviesFromApiAsync(text)
        .then(results => {
            let resultList = [];
            results.forEach(result => {
                if (result.media_type !== 'person') {
                    let { media_type, name, title, release_date, first_air_date, poster_path } = result;
                    if (media_type === 'tv') {
                        title = name;
                        release_date = first_air_date;
                    }
                    const year = release_date.substring(0, 4);
                    const resultItem = {
                        title,
                        year,
                        poster: poster_path
                    };
                    resultList.push(resultItem);
                }
            });
            console.log(resultList);
            setSearchResults(resultList);
        });
    }

    //use debounce when sending request to minimize api hits
    const debouncedGetDetails = useCallback(
        debounce(text => getDetails(text), 2000),
        []
    );

    const onChangeText = (text) => {
        setSearch(text);
    };

    return (
        <StyledCenteredSafeArea>
            <StyledTextInput
                autoFocus={true}
                onChangeText={text => onChangeText(text)}
            />
            <Picker
                selectedValue={pictureQuality}
                style={{ height: 50, width: 150 }}
                onValueChange={(itemValue) => {
                    setPictureQuality(itemValue);
                }
                }>
                <Picker.Item label="SD" value="sd" />
                <Picker.Item label="HD" value="hd" />
                <Picker.Item label="4K" value="4k" />
            </Picker>

            <Picker
                selectedValue={format}
                style={{ height: 50, width: 150 }}
                onValueChange={(itemValue) =>
                    setFormat(itemValue)
                }>
                <Picker.Item label="Physical" value="physical" />
                <Picker.Item label="Digital" value="digital" />
            </Picker>

            {
                searchResults &&
                searchResults.map(item =>
                    <StyledRowView>
                        <StyledSmallText>
                            {item.title}
                        </StyledSmallText>
                        <StyledSmallText>
                            {item.year}
                        </StyledSmallText>
                    </StyledRowView>

                )
            }

            <StyledRoundedButton>
                <StyledButtonText>Add to Collection</StyledButtonText>
            </StyledRoundedButton>
        </StyledCenteredSafeArea>
    );
}

export default AddItem;
