import React, {useState} from 'react';
import { Picker } from '@react-native-picker/picker';
import { StyledButtonText, StyledCenteredSafeArea, StyledRoundedButton, StyledTextInput } from './config/globalStyles';

const AddItem = () => {
    const [format, setFormat] = useState(null);
    const [pictureQuality, setPictureQuality] = useState(null);

    function onChangeText(text) {
        // async fetch from external movie API
        console.log(text);
    }

    return (
        <StyledCenteredSafeArea>
            <StyledTextInput
                autoFocus={true}
                onChangeText={text => onChangeText(text)}
            />

            <Picker
                selectedValue={pictureQuality}
                style={{ height: 50, width: 150 }}
                onValueChange={(itemValue) =>
                    setPictureQuality({ itemValue })
                }>
                <Picker.Item label="SD" value="sd" />
                <Picker.Item label="HD" value="hd" />
                <Picker.Item label="4K" value="4k" />
            </Picker>

            <Picker
                selectedValue={format}
                style={{ height: 50, width: 150 }}
                onValueChange={(itemValue) =>
                    setFormat({ format: itemValue })
                }>
                <Picker.Item label="Physical" value="physical" />
                <Picker.Item label="Digital" value="digital" />
            </Picker>

            <StyledRoundedButton>
                <StyledButtonText>Add to Collection</StyledButtonText>
            </StyledRoundedButton>
        </StyledCenteredSafeArea>
    );
}

export default AddItem;
